(571, 'WPLC_SETTINGS', 'a:47:{s:19:\"wplc_settings_align\";s:1:\"2\";s:21:\"wplc_settings_enabled\";s:1:\"1\";s:20:\"wplc_powered_by_link\";s:1:\"0\";s:18:\"wplc_settings_fill\";s:6:\"ed832f\";s:18:\"wplc_settings_font\";s:6:\"FFFFFF\";s:20:\"wplc_settings_color1\";s:6:\"ED832F\";s:20:\"wplc_settings_color2\";s:6:\"FFFFFF\";s:20:\"wplc_settings_color3\";s:6:\"EEEEEE\";s:20:\"wplc_settings_color4\";s:6:\"666666\";s:10:\"wplc_theme\";s:13:\"theme-default\";s:13:\"wplc_newtheme\";s:7:\"theme-2\";s:22:\"wplc_require_user_info\";s:1:\"1\";s:23:\"wplc_loggedin_user_info\";s:1:\"1\";s:26:\"wplc_user_alternative_text\";s:60:\"Please click \\\'Start Chat\\\' to initiate a chat with an agent\";s:30:\"wplc_user_default_visitor_name\";s:5:\"Guest\";s:22:\"wplc_enabled_on_mobile\";s:1:\"1\";s:17:\"wplc_display_name\";s:1:\"1\";s:22:\"wplc_record_ip_address\";s:1:\"0\";s:27:\"wplc_pro_chat_email_address\";s:21:\"jagreenlern@gmail.com\";s:13:\"wplc_pro_fst1\";s:10:\"Questions?\";s:13:\"wplc_pro_fst2\";s:12:\"Chat with us\";s:13:\"wplc_pro_fst3\";s:15:\"Start live chat\";s:13:\"wplc_pro_sst1\";s:10:\"Start Chat\";s:20:\"wplc_pro_sst1_survey\";s:23:\"Or chat to an agent now\";s:21:\"wplc_pro_sst1e_survey\";s:10:\"Chat ended\";s:13:\"wplc_pro_sst2\";s:32:\"Connecting. Please be patient...\";s:13:\"wplc_pro_tst1\";s:34:\"Reactivating your previous chat...\";s:11:\"wplc_pro_na\";s:29:\"Chat offline. Leave a message\";s:14:\"wplc_pro_intro\";s:56:\"Hello. Please input your details so that I may help you.\";s:17:\"wplc_pro_offline1\";s:83:\"We are currently offline. Please leave a message and we\'ll get back to you shortly.\";s:17:\"wplc_pro_offline2\";s:18:\"Sending message...\";s:17:\"wplc_pro_offline3\";s:55:\"Thank you for your message. We will be in contact soon.\";s:20:\"wplc_pro_offline_btn\";s:15:\"Leave a message\";s:25:\"wplc_pro_offline_btn_send\";s:12:\"Send message\";s:15:\"wplc_user_enter\";s:32:\"Press ENTER to send your message\";s:20:\"wplc_text_chat_ended\";s:40:\"The chat has been ended by the operator.\";s:19:\"wplc_close_btn_text\";s:5:\"close\";s:22:\"wplc_user_welcome_chat\";s:28:\"Welcome. How may I help you?\";s:16:\"wplc_welcome_msg\";s:84:\"Please standby for an agent. While you wait for the agent you may type your message.\";s:20:\"wplc_use_node_server\";i:1;s:27:\"wplc_preferred_gif_provider\";i:1;s:18:\"wplc_giphy_api_key\";s:0:\"\";s:18:\"wplc_tenor_api_key\";s:0:\"\";s:21:\"wplc_enable_msg_sound\";s:1:\"1\";s:24:\"wplc_enable_font_awesome\";s:1:\"1\";s:30:\"wplc_using_localization_plugin\";i:0;s:17:\"wplc_gdpr_enabled\";s:1:\"0\";}', 'yes'),
