(1, 1579621601, 1579621601, 'new', 'wfUpgrade', 75, 'f52a79d2bfc8fd99b193380613866fe5', 'f52a79d2bfc8fd99b193380613866fe5', 'Your WordPress version is out of date', 'WordPress version 5.3.2 is now available. Please upgrade immediately to get the latest security updates from WordPress.', 'a:2:{s:14:\"currentVersion\";s:5:\"5.1.1\";s:10:\"newVersion\";s:5:\"5.3.2\";}'),
(3, 1579621601, 1579621601, 'new', 'wfPluginUpgrade', 50, '2ea63c708447639eb74ab36a88a8cd13', '2ea63c708447639eb74ab36a88a8cd13', 'The Plugin \"Akismet Anti-Spam\" needs an upgrade (4.1.1 -> 4.1.3).', 'You need to upgrade \"Akismet Anti-Spam\" to the newest version to ensure you have any security fixes the developer has released.', 'a:19:{s:20:\"WC requires at least\";s:0:\"\";s:15:\"WC tested up to\";s:0:\"\";s:3:\"Woo\";s:0:\"\";s:4:\"Name\";s:17:\"Akismet Anti-Spam\";s:9:\"PluginURI\";s:20:\"https://akismet.com/\";s:7:\"Version\";s:5:\"4.1.1\";s:11:\"Description\";s:372:\"Utilizado por millones, Akismet es, posiblemente, la mejor manera del mundo de  <strong>proteger tu blog del spam</strong>. Mantiene tu blog protegido incluso cuando duermes. Para comenzar: activa el plugin Akismet y ve a la página de ajustes de Akismet para establecer tu clave de API. <cite>Por <a href=\"https://automattic.com/wordpress-plugins/\">Automattic</a>.</cite>\";s:6:\"Author\";s:66:\"<a href=\"https://automattic.com/wordpress-plugins/\">Automattic</a>\";s:9:\"AuthorURI\";s:41:\"https://automattic.com/wordpress-plugins/\";s:10:\"TextDomain\";s:7:\"akismet\";s:10:\"DomainPath\";s:0:\"\";s:7:\"Network\";b:0;s:5:\"Title\";s:52:\"<a href=\"https://akismet.com/\">Akismet Anti-Spam</a>\";s:10:\"AuthorName\";s:10:\"Automattic\";s:10:\"pluginFile\";s:64:\"C:/xampp/htdocs/wordpress/wp-content/plugins/akismet/akismet.php\";s:10:\"newVersion\";s:5:\"4.1.3\";s:4:\"slug\";s:7:\"akismet\";s:5:\"wpURL\";s:37:\"https://wordpress.org/plugins/akismet\";s:10:\"vulnerable\";b:0;}'),
(4, 1579621602, 1579621602, 'new', 'wfPluginUpgrade', 100, '5bd359a1d2506e7edec9203b1045afaf', '5bd359a1d2506e7edec9203b1045afaf', 'The Plugin \"All In One SEO Pack\" needs an upgrade (2.12.1 -> 3.3.4).', 'You need to upgrade \"All In One SEO Pack\" to the newest version to ensure you have any security fixes the developer has released.', 'a:20:{s:20:\"WC requires at least\";s:0:\"\";s:15:\"WC tested up to\";s:0:\"\";s:3:\"Woo\";s:0:\"\";s:4:\"Name\";s:19:\"All In One SEO Pack\";s:9:\"PluginURI\";s:58:\"https://semperplugins.com/all-in-one-seo-pack-pro-version/\";s:7:\"Version\";s:6:\"2.12.1\";s:11:\"Description\";s:321:\"Out-of-the-box SEO for your WordPress blog. Features like XML Sitemaps, SEO for custom post types, SEO for blogs or business sites, SEO for ecommerce sites, and much more. More than 50 million downloads since 2007. <cite>Por <a href=\"https://semperplugins.com/all-in-one-seo-pack-pro-version/\">Michael Torbert</a>.</cite>\";s:6:\"Author\";s:88:\"<a href=\"https://semperplugins.com/all-in-one-seo-pack-pro-version/\">Michael Torbert</a>\";s:9:\"AuthorURI\";s:58:\"https://semperplugins.com/all-in-one-seo-pack-pro-version/\";s:10:\"TextDomain\";s:19:\"all-in-one-seo-pack\";s:10:\"DomainPath\";s:6:\"/i18n/\";s:7:\"Network\";b:0;s:5:\"Title\";s:92:\"<a href=\"https://semperplugins.com/all-in-one-seo-pack-pro-version/\">All In One SEO Pack</a>\";s:10:\"AuthorName\";s:15:\"Michael Torbert\";s:10:\"pluginFile\";s:88:\"C:/xampp/htdocs/wordpress/wp-content/plugins/all-in-one-seo-pack/all_in_one_seo_pack.php\";s:10:\"newVersion\";s:5:\"3.3.4\";s:4:\"slug\";s:19:\"all-in-one-seo-pack\";s:5:\"wpURL\";s:49:\"https://wordpress.org/plugins/all-in-one-seo-pack\";s:10:\"vulnerable\";b:1;s:17:\"vulnerabilityLink\";s:41:\"https://wpvulndb.com/vulnerabilities/9915\";}'),
(5, 1579621602, 1579621602, 'new', 'wfPluginUpgrade', 50, '4c1a8d54c6e28da556fba6a31bc705f1', '4c1a8d54c6e28da556fba6a31bc705f1', 'The Plugin \"bbPress\" needs an upgrade (2.5.14 -> 2.6.3).', 'You need to upgrade \"bbPress\" to the newest version to ensure you have any security fixes the developer has released.', 'a:19:{s:20:\"WC requires at least\";s:0:\"\";s:15:\"WC tested up to\";s:0:\"\";s:3:\"Woo\";s:0:\"\";s:4:\"Name\";s:7:\"bbPress\";s:9:\"PluginURI\";s:19:\"https://bbpress.org\";s:7:\"Version\";s:6:\"2.5.14\";s:11:\"Description\";s:144:\"bbPress is forum software with a twist from the creators of WordPress. <cite>Por <a href=\"https://bbpress.org\">The bbPress Community</a>.</cite>\";s:6:\"Author\";s:55:\"<a href=\"https://bbpress.org\">The bbPress Community</a>\";s:9:\"AuthorURI\";s:19:\"https://bbpress.org\";s:10:\"TextDomain\";s:7:\"bbpress\";s:10:\"DomainPath\";s:11:\"/languages/\";s:7:\"Network\";b:0;s:5:\"Title\";s:41:\"<a href=\"https://bbpress.org\">bbPress</a>\";s:10:\"AuthorName\";s:21:\"The bbPress Community\";s:10:\"pluginFile\";s:64:\"C:/xampp/htdocs/wordpress/wp-content/plugins/bbpress/bbpress.php\";s:10:\"newVersion\";s:5:\"2.6.3\";s:4:\"slug\";s:7:\"bbpress\";s:5:\"wpURL\";s:37:\"https://wordpress.org/plugins/bbpress\";s:10:\"vulnerable\";b:0;}'),
(6, 1579621602, 1579621602, 'new', 'wfPluginUpgrade', 50, '3c046cc01883808ed0bf59101138b655', '3c046cc01883808ed0bf59101138b655', 'The Plugin \"Contact Form 7\" needs an upgrade (5.1.1 -> 5.1.6).', 'You need to upgrade \"Contact Form 7\" to the newest version to ensure you have any security fixes the developer has released.', 'a:19:{s:20:\"WC requires at least\";s:0:\"\";s:15:\"WC tested up to\";s:0:\"\";s:3:\"Woo\";s:0:\"\";s:4:\"Name\";s:14:\"Contact Form 7\";s:9:\"PluginURI\";s:25:\"https://contactform7.com/\";s:7:\"Version\";s:5:\"5.1.1\";s:11:\"Description\";s:135:\"Just another contact form plugin. Simple but flexible. <cite>Por <a href=\"https://ideasilo.wordpress.com/\">Takayuki Miyoshi</a>.</cite>\";s:6:\"Author\";s:62:\"<a href=\"https://ideasilo.wordpress.com/\">Takayuki Miyoshi</a>\";s:9:\"AuthorURI\";s:31:\"https://ideasilo.wordpress.com/\";s:10:\"TextDomain\";s:14:\"contact-form-7\";s:10:\"DomainPath\";s:11:\"/languages/\";s:7:\"Network\";b:0;s:5:\"Title\";s:54:\"<a href=\"https://contactform7.com/\">Contact Form 7</a>\";s:10:\"AuthorName\";s:16:\"Takayuki Miyoshi\";s:10:\"pluginFile\";s:81:\"C:/xampp/htdocs/wordpress/wp-content/plugins/contact-form-7/wp-contact-form-7.php\";s:10:\"newVersion\";s:5:\"5.1.6\";s:4:\"slug\";s:14:\"contact-form-7\";s:5:\"wpURL\";s:44:\"https://wordpress.org/plugins/contact-form-7\";s:10:\"vulnerable\";b:0;}'),
(7, 1579621602, 1579621602, 'new', 'wfPluginUpgrade', 50, '1af63e2fe77996a517155cd1975cc85b', '1af63e2fe77996a517155cd1975cc85b', 'The Plugin \"Duplicator\" needs an upgrade (1.3.12 -> 1.3.24).', 'You need to upgrade \"Duplicator\" to the newest version to ensure you have any security fixes the developer has released.', 'a:19:{s:20:\"WC requires at least\";s:0:\"\";s:15:\"WC tested up to\";s:0:\"\";s:3:\"Woo\";s:0:\"\";s:4:\"Name\";s:10:\"Duplicator\";s:9:\"PluginURI\";s:49:\"https://snapcreek.com/duplicator/duplicator-free/\";s:7:\"Version\";s:6:\"1.3.12\";s:11:\"Description\";s:207:\"Migrate and backup a copy of your WordPress files and database. Duplicate and move a site from one location to another quickly. <cite>Por <a href=\"http://www.snapcreek.com/duplicator/\">Snap Creek</a>.</cite>\";s:6:\"Author\";s:61:\"<a href=\"http://www.snapcreek.com/duplicator/\">Snap Creek</a>\";s:9:\"AuthorURI\";s:36:\"http://www.snapcreek.com/duplicator/\";s:10:\"TextDomain\";s:10:\"duplicator\";s:10:\"DomainPath\";s:0:\"\";s:7:\"Network\";b:0;s:5:\"Title\";s:74:\"<a href=\"https://snapcreek.com/duplicator/duplicator-free/\">Duplicator</a>\";s:10:\"AuthorName\";s:10:\"Snap Creek\";s:10:\"pluginFile\";s:70:\"C:/xampp/htdocs/wordpress/wp-content/plugins/duplicator/duplicator.php\";s:10:\"newVersion\";s:6:\"1.3.24\";s:4:\"slug\";s:10:\"duplicator\";s:5:\"wpURL\";s:40:\"https://wordpress.org/plugins/duplicator\";s:10:\"vulnerable\";b:0;}'),
